import LikeMeme from '../../src/application/usecases/LikeMeme'
import PublishMeme from '../../src/application/usecases/PublishMeme'
import SignUp from '../../src/application/usecases/SignUp'
import Id from '../../src/domain/Id'
import MemeRepositoryInMemory from '../../src/infra/repositories/MemeRepositoryInMemory'
import UserRepositoryInMemory from '../../src/infra/repositories/UserRepositoryInMemory'

let signUp: SignUp
let publishMeme: PublishMeme
let likeMeme: LikeMeme
let userRepositoryInMemory: UserRepositoryInMemory
let memeRepositoryInMemory: MemeRepositoryInMemory

beforeEach(() => {
  userRepositoryInMemory = new UserRepositoryInMemory()
  memeRepositoryInMemory = new MemeRepositoryInMemory()
  signUp = new SignUp(userRepositoryInMemory)
  publishMeme = new PublishMeme(memeRepositoryInMemory)
  likeMeme = new LikeMeme(memeRepositoryInMemory)
})

test('it should not be able like meme if meme not found', async () => {
  const inputSignUp = {
    username: 'wftDeNome',
    email: 'wftdenome@email.com',
    password: 'S3nh@MtS3gur@',
    passwordAgain: 'S3nh@MtS3gur@',
  }
  const outputSignUp = await signUp.handle(inputSignUp)

  const inputLikeMeme = {
    userId: outputSignUp.userId,
    memeId: '6173ee30-ff14-4854-84a0-59a7cf724f6a',
  }
  await expect(() => likeMeme.handle(inputLikeMeme)).rejects.toThrow(
    'Conteúdo não encontrado',
  )
})

test('it should not be able like meme if already liked this meme', async () => {
  const inputSignUp = {
    username: 'wftDeNome',
    email: 'wftdenome@email.com',
    password: 'S3nh@MtS3gur@',
    passwordAgain: 'S3nh@MtS3gur@',
  }
  const outputSignUp = await signUp.handle(inputSignUp)

  const inputPublishMeme = {
    authorId: outputSignUp.userId,
    content:
      'data:video/mp4;base64,',
    tags: [{ name: 'one piece' }, { name: 'brook' }],
  }
  const outputPublishMeme = await publishMeme.handle(inputPublishMeme)

  const inputLikeMeme = {
    userId: outputSignUp.userId,
    memeId: outputPublishMeme.memeId,
  }
  await likeMeme.handle(inputLikeMeme)

  await expect(() => likeMeme.handle(inputLikeMeme)).rejects.toThrow(
    'Você já deu like neste meme',
  )
})

test('it should be able like meme', async () => {
  const inputSignUp = {
    username: 'wftDeNome',
    email: 'wftdenome@email.com',
    password: 'S3nh@MtS3gur@',
    passwordAgain: 'S3nh@MtS3gur@',
  }
  const outputSignUp = await signUp.handle(inputSignUp)

  const inputPublishMeme = {
    authorId: outputSignUp.userId,
    content:
      'data:video/mp4;base64,',
    tags: [{ name: 'one piece' }, { name: 'brook' }],
  }
  const outputPublishMeme = await publishMeme.handle(inputPublishMeme)

  const inputLikeMeme = {
    userId: outputSignUp.userId,
    memeId: outputPublishMeme.memeId,
  }
  await likeMeme.handle(inputLikeMeme)

  const result = await memeRepositoryInMemory.alreadyLikeThisMeme(
    new Id(inputLikeMeme.userId),
    new Id(inputLikeMeme.memeId),
  )
  expect(result).toStrictEqual(true)
})
