import { describe, beforeEach, jest, expect, test } from 'bun:test'

import { Id } from '../../../src/domain/value-objects/id/model/id-value-object'
import { RepubMemeUseCase } from '../../../src/usecases/folder/repub-meme-use-case'
import { Folder } from '../../../src/domain/entities/folder/model/folder'
import { FolderGateway } from '../../../src/domain/entities/folder/gateways/folder-gateway'
import { Image } from '../../../src/domain/value-objects/image/model/image-value-object'

describe('Suite test for testing repub meme use case', () => {
  let folder: Folder
  let gateway: FolderGateway
  let repubMemeUseCase: RepubMemeUseCase

  beforeEach(() => {
    folder = new Folder(
      new Id('e08d894c-0c31-47f0-8add-14ce9470b51e'), 
      'Repubs', 
      new Image('data:image/jpeg;base64,')
    )

    gateway = {
      save: jest.fn().mockImplementationOnce(() => Promise.resolve()),
      find: () => new Promise((resolve) => resolve(folder)),
      findAll: () => new Promise((resolve) => resolve([folder])),
      alreadyFolderWithThisName: () => new Promise((resolve) => resolve(false)),
      repubMeme: jest.fn().mockImplementationOnce(() => Promise.resolve()),
    } satisfies FolderGateway

    repubMemeUseCase = new RepubMemeUseCase(gateway)
  })

  test('it should be able repub meme', async () => {
    const input = {
      data: {
        userId: '8f451c11-ef44-4970-a4f8-e083ed6f5c62',
        folderId: folder.id.toString(),
        memeId: 'd262ab0d-6d32-4a12-a651-06af51c40a69'
      },
    }

    await expect(await repubMemeUseCase.handle(input)).toStrictEqual({
      status: 'SUCCESS',
      data: {},
    })
  })
})
