import { describe, beforeEach, jest, expect, test } from 'bun:test'

import { Id } from '../../../src/domain/value-objects/id/model/id-value-object'
import { MemeGateway } from '../../../src/domain/entities/meme/gateways/meme-gateway'
import { Meme } from '.../../../src/domain/entities/meme/model/meme'
import { MemeRecommendationUseCase } from '../../../src/usecases/meme/meme-recommendation-use-case'
import { Midia } from '../../../src/domain/value-objects/midia/model/midia-value-object'

describe('Suite test for testing meme recommendation use case', () => {
  let meme01: Meme
  let meme02: Meme
  let memes: Array<Meme>
  let gateway: MemeGateway
  let memeRecommendationUseCase: MemeRecommendationUseCase

  beforeEach(() => {
    meme01 = new Meme(
      new Id('d262ab0d-6d32-4a12-a651-06af51c40a69'),
      new Id('5d86c647-3265-4cf4-babb-e5ce353a446a'),
      new Midia('data:image/jpeg;base64,'),
      [
        { name: 'nazismo', weight: 1 },
      ],
    )
    meme02 = new Meme(
      new Id('7996dc77-4da0-4cab-b172-1296ed891800'),
      new Id('8de4254c-f75f-4a77-a885-7b41de1b0cea'),
      new Midia('data:video/mp4;base64,'),
      [
        { name: 'nazismo', weight: 1 },
        { name: 'Ã©', weight: 1 },
        { name: 'um', weight: 1 },
        { name: 'lixo', weight: 1 },
      ],
    )

    memes = [meme01, meme02]

    gateway = {
      publish: jest.fn().mockImplementationOnce(() => Promise.resolve()),
      find: (id) => {
        let result: Meme | null = null
        memes.forEach(meme => {
          result = meme.id.toString() == id.toString() ? meme : null
        })

        return new Promise((resolve) => resolve(result))
      },
      recentMemesNotLikedByUser: () => new Promise((resolve) => resolve([meme02])),
      lastLikes: () => new Promise((resolve) => resolve([meme01])),
      like: jest.fn().mockImplementationOnce(() => Promise.resolve()),
      alreadyLikedMeme: jest.fn().mockImplementationOnce(() => Promise.resolve(true)),
      highlights: () => new Promise((resolve) => resolve(memes)),
      collective: () => new Promise((resolve) => resolve(memes)),
      getMemeInFolder: () => new Promise((resolve) => resolve([meme01])),
    } satisfies MemeGateway

    memeRecommendationUseCase = new MemeRecommendationUseCase(gateway)
  })

  test('it should be able receive memes recommendation', async () => {
    const input = {
      data: {
        userId: '8f451c11-ef44-4970-a4f8-e083ed6f5c62',
      },
    }

    await expect(await memeRecommendationUseCase.handle(input)).toStrictEqual({
      status: 'SUCCESS',
      data: {
        recommendations: [meme02],
      },
    })
  })
})
